import React, { useState, useEffect, useRef } from "react"; import "./EmuSchermo.css";

// Costanti per la fisica della pallina
const horizontalAcceleration = 0.2;
const maxHorizontalSpeed = 2;
const friction = 0.1;
const jumpVelocity = 7;
const gravity = -0.5;

const BALL_SIZE = 5; // dimensione in percentuale (larghezza e altezza)

function EmuSchermo() { // Stato della pallina: posizione, velocità, stato sul pavimento
const [ball, setBall] = useState({ x: 50, 
 y: 0,
vx: 0,
 vy: 0, 
 isOnGround: true, });

// Stato per le vite
const [lives, setLives] = useState(3);

// Array di oggetti che rappresentano piattaforme volanti e parti spinate
// Le piattaforme hanno proprietà: x, y, width, height, dx (velocità orizzontale) e type ("platform" oppure "spike")
const [platforms, setPlatforms] = useState([
    { id: 1, x: 10, y: 30, width: 20, height: 5, dx: 0.15, type: "platform" },
     { id: 2, x: 60, y: 50, width: 20, height: 5, dx: -0.2, type: "platform" },
      { id: 3, x: 40, y: 70, width: 15, height: 5, dx: 0.1, type: "spike" }
    ]);

// Riferimento per tenere traccia dei tasti premuti

const keysPressedRef = useRef({});

useEffect(() => { const handleKeyDown = (e) => { keysPressedRef.current[e.code] = true; };
const handleKeyUp = (e) => {
    keysPressedRef.current[e.code] = false;
  };

  window.addEventListener("keydown", handleKeyDown);
  window.addEventListener("keyup", handleKeyUp);
  return () => {
    window.removeEventListener("keydown", handleKeyDown);
    window.removeEventListener("keyup", handleKeyUp);
  };
}, []);

const animationFrameRef = useRef(null);

// Funzione per il controllo delle collisioni tra la pallina e un rettangolo (piattaforma o spike)
const isColliding = (ballObj, rect) => { // Approssimiamo la pallina come un quadrato di dimensione BALL_SIZE 
return ( ballObj.x < rect.x + rect.width && ballObj.x + BALL_SIZE > rect.x && ballObj.y < rect.y + rect.height && ballObj.y + BALL_SIZE > rect.y ); };

// Funzione per resettare la pallina (in caso di collisione con uno spike)
const resetBall = () => { setBall({ x: 50, y: 0, vx: 0, vy: 0, isOnGround: true }); };

useEffect(() => { const update = () => { setBall(prev => { let { x, y, vx, vy, isOnGround } = prev; const keys = keysPressedRef.current;
        // Movimento orizzontale con le frecce
        if (keys["ArrowLeft"]) {
            vx -= horizontalAcceleration;
          }
          if (keys["ArrowRight"]) {
            vx += horizontalAcceleration;
          }
          // Applica attrito se nessun tasto laterale è premuto
          if (!keys["ArrowLeft"] && !keys["ArrowRight"]) {
            if (vx > 0) vx = Math.max(vx - friction, 0);
            if (vx < 0) vx = Math.min(vx + friction, 0);
          }
          // Limita la velocità orizzontale
          vx = Math.max(Math.min(vx, maxHorizontalSpeed), -maxHorizontalSpeed);

          // Salto: se viene premuta la barra spaziatrice e la pallina è sul pavimento o su una piattaforma
          if (keys["Space"] && isOnGround) {
            vy = jumpVelocity;
            isOnGround = false;
          }

          // Applica gravità
          vy += gravity;

          // Aggiorna le posizioni
          x += vx;
          y += vy;

          // Gestione della collisione con il "pavimento" (y = 0)
          if (y < 0) {
            y = 0;
            vy = 0;
            isOnGround = true;
          }

          // Limita il movimento orizzontale nel contenitore (da 0% a 95%)
          if (x < 0) {
            x = 0;
            vx = 0;
          }
          if (x > 95) {
            x = 95;
            vx = 0;
          }

          // Controlla le collisioni con ogni piattaforma/spike
          platforms.forEach(p => {
            if (isColliding({x, y}, p)) {
              if (p.type === "platform") {
                // Se la pallina sta atterrando (vy negativo) e colpisce dall'alto, "atterra" sulla piattaforma
                if (vy <= 0 && y >= p.y + p.height - 2) {
                  y = p.y + p.height;
                  vy = 0;
                  isOnGround = true;
                }
              } else if (p.type === "spike") {
                // Collisione con uno spike: toglie una vita e resetta la pallina
                setLives(prevLives => Math.max(prevLives - 1, 0));
                resetBall();
              }
            }
          });

          return { x, y, vx, vy, isOnGround };
        });

        // Aggiorna le posizioni delle piattaforme
        setPlatforms(prevPlatforms => prevPlatforms.map(p => {
          let newX = p.x + p.dx;
          // Inverte direzione se raggiunge il bordo (0% o 100% - larghezza)
          if (newX < 0 || newX + p.width > 100) {
            p.dx = -p.dx;
            newX = p.x + p.dx;
          }
          return { ...p, x: newX };
        }));

        // Controlla se il gioco è finito (lives esaurite)
        if (lives <= 0) {
          cancelAnimationFrame(animationFrameRef.current);
          // Puoi aggiungere logica per la "game over" (es. mostrare un messaggio o un bottone per riprovare)
          return;
        }

        animationFrameRef.current = requestAnimationFrame(update);
      };

      animationFrameRef.current = requestAnimationFrame(update);
      return () => cancelAnimationFrame(animationFrameRef.current);
    }, [lives, platforms]);

    return ( <div className="emu-container"> {/* Mostra il numero di vite */} <div className="lives-display">Vite: {lives}</div>
      {/* Render della pallina */}
  <div
    className="ball"
    style={{
      left: `${ball.x}%`,
      bottom: `${ball.y}%`
    }}
  />

  {/* Render delle piattaforme e degli spike */}
  {platforms.map(p => (
    <div
      key={p.id}
      className={p.type === "platform" ? "platform" : "spike"}
      style={{
        left: `${p.x}%`,
        bottom: `${p.y}%`,
        width: `${p.width}%`,
        height: `${p.height}%`
      }}
    />
  ))}
</div>
); 
}

export default EmuSchermo;
