import React, { useState, useEffect, useRef } from "react";
import "./EmuSchermo.css";

const BUTTONS = {
  A: 1,
  B: 2,
  START: 3,
  SELECT: 4,
  LEFT: 7,
  RIGHT: 8,
};

const LEVEL = [
  { type: "p", height: 20, posStartX: 30, posEndX: 50 },
  { type: "p", height: 35, posStartX: 50, posEndX: 70 },
  { type: "s", height: 60, posStartX: 70, posEndX: 90 },
  { type: "p", height: 60, posStartX: 20, posEndX: 60 },
];


function createSeededRandom(seed) {
    // Implementazione di un generatore congruenziale lineare (LCG)
    const a = 1664525;
    const c = 1013904223;
    const m = 2**32;
    let state = seed;

    return function() {
        state = (a * state + c) % m;
        return state / m;
    };
}

function generateLevel(seed) {
    const rand = createSeededRandom(seed);
    const platforms = [];
    let currentHeight = 20;
    const maxX = 100;  // Larghezza massima del livello

    // Parametri configurabili
    const platformConfig = {
        typeChance: { p: 0.8, s: 0.2 },      // Probabilità tipi piattaforma
        heightRange: [10, 30],               // Range incremento altezza
        widthRange: [20, 40],               // Range larghezza piattaforma
        spacing: [20, 60]                  // Spaziatura orizzontale minima/massima
    };

    // Genera 4 piattaforme come nell'esempio
    for (let i = 0; i < 4; i++) {
        // Tipo piattaforma
        const typeRand = rand();
        const type = typeRand < platformConfig.typeChance.s ? 's' : 'p';

        // Altezza
        if (i > 0) {
            const heightIncrement = Math.floor(
                rand() * (platformConfig.heightRange[1] - platformConfig.heightRange[0] + 1)
            ) + platformConfig.heightRange[0];
            currentHeight += heightIncrement;
        }

        // Larghezza piattaforma
        const width = Math.floor(
            rand() * (platformConfig.widthRange[1] - platformConfig.widthRange[0] + 1)
        ) + platformConfig.widthRange[0];

        // Posizione X
        const posStartX = Math.floor(rand() * (maxX - width));
        const posEndX = posStartX + width;

        platforms.push({
            type: type,
            height: currentHeight,
            posStartX: posStartX,
            posEndX: posEndX
        });
    }

    return platforms;
}

