      {LEVEL.map((item, i) => (
        item.type === "p" ? (
          <div
            key={i}
            className="platform"
            style={{
              left: `${item.posStartX}%`,
              width: `${item.posEndX - item.posStartX}%`,
              bottom: `${item.height}%`,
              height: "2%",
            }}
          />
        ) : (
          <div
