import React, { useState, useEffect, useRef } from "react";
import "./EmuSchermo.css";
import { GAME_LEVELS } from "./Levels"; // Importa i livelli dal file creato

const BUTTONS = {
  A: 1,
  B: 2,
  START: 3,
  SELECT: 4,
  UP: 5,
  DOWN: 6,
  LEFT: 7,
  RIGHT: 8,
};

const LEVEL_TYPES = {
  PLATFORM: "platform",
  MOVING: "moving",
  BOUNCE: "bounce",
  ENEMY: "enemy",
  FINISH: "finish",
  SPIKE: "spike",
  DISAPPEARING: "disappear"
};

function EmuSchermo({ pressedButtons }) {
  // Costanti di fisica espresse in percentuali
  const ballSize = 6;
  const horizontalAcceleration = 0.2;
  const maxHorizontalSpeed = 2;
  const friction = 0.1;
  const jumpVelocity = 7;
  const gravity = -0.5;
  const bounceMultiplier = 1; // Moltiplicatore per piattaforme rimbalzanti

  // Stato di gioco
  const [currentLevel, setCurrentLevel] = useState(0);
  const [gameState, setGameState] = useState({
    lives: 3,
    score: 0,
    isGameOver: false,
    isLevelComplete: false
  });

  // Array per tenere traccia degli oggetti collezionabili raccolti
  const [collectedItems, setCollectedItems] = useState([]);

  // Stato delle piattaforme che scompaiono
  const [disappearingPlatforms, setDisappearingPlatforms] = useState({});

  // Riferimento al timer per piattaforme che scompaiono
  const disappearingTimersRef = useRef({});



  // Stato della pallina
  const [ball, setBall] = useState({
    x: 0,
    y: 0,
    vx: 0,
    vy: 0,
    isOnGround: false,
    isJumping: false
  });

  // Inizializza la posizione della pallina all'inizio del livello
  useEffect(() => {
    if (GAME_LEVELS[currentLevel]) {
      const { startPosition } = GAME_LEVELS[currentLevel];
      setBall(prev => ({
        ...prev,
        x: startPosition.x,
        y: startPosition.y,
        vx: 0,
        vy: 0,
        isOnGround: false,
        isJumping: false
      }));

      // Resetta lo stato delle piattaforme che scompaiono
      setDisappearingPlatforms({});

      // Cancella i timer esistenti
      Object.values(disappearingTimersRef.current).forEach(timer => clearTimeout(timer));
      disappearingTimersRef.current = {};

      // Resetta gli oggetti collezionabili raccolti
      setCollectedItems([]);

      // Aggiungi classe per l'animazione di transizione
      const container = document.querySelector('.emu-container');
      if (container) {
        container.classList.add('level-transition');
        setTimeout(() => {
          container.classList.remove('level-transition');
        }, 500);
      }
    }
  }, [currentLevel]);

  // Usiamo un ref per tenere traccia dell'ultimo valore di pressedButtons
  const pressedButtonsRef = useRef(pressedButtons);
  useEffect(() => {
    pressedButtonsRef.current = pressedButtons;
  }, [pressedButtons]);

  // Verifica se c'è una collisione con una piattaforma
  const checkPlatformCollision = (x, y, vx, vy) => {
    if (!GAME_LEVELS[currentLevel]) return { collision: false };

    const elements = GAME_LEVELS[currentLevel].elements;
    const ballBottom = y;
    const ballTop = y + ballSize;
    const ballLeft = x;
    const ballRight = x + ballSize;
    const tolerance = 2; // Tolleranza per una collisione più precisa

    for (let i = 0; i < elements.length; i++) {
      const element = elements[i];

      // Salta se la piattaforma è scomparsa
      if (element.type === LEVEL_TYPES.DISAPPEARING && disappearingPlatforms[i]) {
        continue;
      }

      if (element.type === LEVEL_TYPES.PLATFORM || 
          element.type === LEVEL_TYPES.MOVING || 
          element.type === LEVEL_TYPES.BOUNCE || 
          element.type === LEVEL_TYPES.DISAPPEARING) {

        // Ottieni posizione effettiva per piattaforme in movimento
        let elementX = element.x;
        let elementY = element.y;

        if (element.type === LEVEL_TYPES.MOVING) {
          const time = Date.now() / 1000;
          const cycle = Math.sin(time * element.speed);

          elementX = element.x + element.moveX * cycle;
          elementY = element.y + element.moveY * cycle;
        }

        const elementLeft = elementX;
        const elementRight = elementX + element.width;
        const elementTop = elementY + element.height;
        const elementBottom = elementY;

        // Verifica se c'è una sovrapposizione tra la pallina e la piattaforma
        if (ballRight > elementLeft && 
            ballLeft < elementRight && 
            ballTop > elementBottom && 
            ballBottom < elementTop) {

          // Calcola le distanze di penetrazione
          const fromTop = ballBottom - elementTop;
          const fromBottom = elementBottom - ballTop;
          const fromLeft = ballRight - elementLeft;
          const fromRight = elementRight - ballLeft;

          // Trova la direzione di minima penetrazione
          const minPenetration = Math.min(Math.abs(fromTop), Math.abs(fromBottom), Math.abs(fromLeft), Math.abs(fromRight));

          // Collisione dall'alto (atterraggio sulla piattaforma)
          if (minPenetration === Math.abs(fromTop) && vy < 0) {
            // Fa scomparire la piattaforma se è di tipo DISAPPEARING
            if (element.type === LEVEL_TYPES.DISAPPEARING && !disappearingPlatforms[i]) {
              disappearingTimersRef.current[i] = setTimeout(() => {
                setDisappearingPlatforms(prev => ({ ...prev, [i]: true }));
              }, element.timeout || 1000);
            }

            // Rimbalza se la piattaforma è di tipo BOUNCE
            if (element.type === LEVEL_TYPES.BOUNCE) {
              return { 
                collision: true, 
                y: elementTop, 
                vy: jumpVelocity * (element.power || bounceMultiplier),
                isOnGround: false
              };
            }

            // Atterraggio normale
            return { 
              collision: true, 
              y: elementTop, 
              vy: 0,
              isOnGround: true
            };
          }

          // Collisione dal basso (colpire una piattaforma saltando)
          if (minPenetration === Math.abs(fromBottom) && vy > 0) {
            return {
              collision: true,
              y: elementBottom - ballSize,
              vy: -vy * 0.2, // Rimbalza leggermente verso il basso
              isOnGround: false
            };
          }

          // Collisione da sinistra
          if (minPenetration === Math.abs(fromLeft) && Math.abs(vx) > 0) {
            return {
              collision: true,
              x: elementLeft - ballSize,
              vx: 0
            };
          }

          // Collisione da destra
          if (minPenetration === Math.abs(fromRight) && Math.abs(vx) > 0) {
            return {
              collision: true,
              x: elementRight,
              vx: 0
            };
          }
        }

        // Gestione speciale per l'atterraggio più preciso
        // Verifica se stiamo cadendo appena sopra una piattaforma
        if (vy < 0 && 
            ballBottom >= elementTop - tolerance && 
            ballBottom <= elementTop + tolerance &&
            ballRight > elementLeft + 1 && 
            ballLeft < elementRight - 1) {

          // Fa scomparire la piattaforma se è di tipo DISAPPEARING
          if (element.type === LEVEL_TYPES.DISAPPEARING && !disappearingPlatforms[i]) {
            disappearingTimersRef.current[i] = setTimeout(() => {
              setDisappearingPlatforms(prev => ({ ...prev, [i]: true }));
            }, element.timeout || 1000);
          }

          // Rimbalza se la piattaforma è di tipo BOUNCE
          if (element.type === LEVEL_TYPES.BOUNCE) {
            return { 
              collision: true, 
              y: elementTop, 
              vy: jumpVelocity * (element.power || bounceMultiplier),
              isOnGround: false
            };
          }

          // Atterraggio normale con aggancio alla piattaforma
          return { 
            collision: true, 
            y: elementTop, 
            vy: 0,
            isOnGround: true
          };
        }
      }
    }

    return { collision: false };
  };

  // Controlla se c'è una collisione con un oggetto collezionabile
  const checkCollectableCollision = (x, y) => {
    if (!GAME_LEVELS[currentLevel]) return false;

    const elements = GAME_LEVELS[currentLevel].elements;

    for (let i = 0; i < elements.length; i++) {
      const element = elements[i];

      if (element.type === LEVEL_TYPES.COLLECTABLE) {
        // Salta se l'oggetto è già stato raccolto
        if (collectedItems.includes(i)) continue;

        // Controllo collisione (oggetto collezionabile è un punto)
        const collectableSize = 5; // Dimensione approssimativa dell'oggetto
        if (x < element.x + collectableSize && 
            x + ballSize > element.x && 
            y < element.y + collectableSize && 
            y + ballSize > element.y) {

          // Aggiorna lo stato di gioco
          setGameState(prev => ({
            ...prev,
            score: prev.score + 100,
            collectables: prev.collectables + 1
          }));

          // Aggiorna l'array degli oggetti raccolti
          setCollectedItems(prev => [...prev, i]);

          return true;
        }
      }
    }

    return false;
  };

  // Controlla se c'è una collisione con un nemico o una punta
  const checkHazardCollision = (x, y) => {
    if (!GAME_LEVELS[currentLevel]) return false;

    const elements = GAME_LEVELS[currentLevel].elements;

    for (let i = 0; i < elements.length; i++) {
      const element = elements[i];

      if (element.type === LEVEL_TYPES.ENEMY || element.type === LEVEL_TYPES.SPIKE) {
        let elementX = element.x;
        let elementY = element.y;

        // Aggiorna la posizione per nemici in movimento
        if (element.type === LEVEL_TYPES.ENEMY && element.moveX) {
          const time = Date.now() / 1000;
          const cycle = Math.sin(time * element.speed);
          elementX = element.x + element.moveX * cycle;
        }

        // Controllo collisione
        if (x < elementX + element.width && 
            x + ballSize > elementX && 
            y < elementY + element.height && 
            y + ballSize > elementY) {

          return true;
        }
      }
    }

    return false;
  };

  // Controlla se il giocatore ha raggiunto il traguardo
  const checkFinishCollision = (x, y) => {
    if (!GAME_LEVELS[currentLevel]) return false;

    const elements = GAME_LEVELS[currentLevel].elements;

    for (let i = 0; i < elements.length; i++) {
      const element = elements[i];

      if (element.type === LEVEL_TYPES.FINISH) {
        const finishSize = 10; // Dimensione approssimativa del traguardo
        if (x < element.x + finishSize && 
            x + ballSize > element.x && 
            y < element.y + finishSize && 
            y + ballSize > element.y) {

          return true;
        }
      }
    }

    return false;
  };

  // Gestisce il completamento del livello
  const completeLevel = () => {
    // Verifica se ci sono altri livelli
    if (currentLevel < GAME_LEVELS.length - 1) {
      // Passa al livello successivo
      setCurrentLevel(prev => prev +1);
    } else {
      // Hai completato tutti i livelli
      setGameState(prev => ({
        ...prev,
        isGameOver: true,
        isLevelComplete: true
      }));
    }
  };

  // Gestisce la morte del giocatore
  const playerDeath = () => {    
    // Riduce il numero di vite
    setGameState(prev => {
      const newLives = prev.lives - 1;

      if (newLives <= 0) {
        return {
          ...prev,
          lives: 0,
          isGameOver: true
        };
      }

      return {
        ...prev,
        lives: newLives
      };
    });



    // Reimposta la posizione della pallina all'inizio del livello
    if (GAME_LEVELS[currentLevel]) {
      const { startPosition } = GAME_LEVELS[currentLevel];
      setBall(prev => ({
        ...prev,
        x: startPosition.x,
        y: startPosition.y,
        vx: 0,
        vy: 0,
        isOnGround: false,
        isJumping: false
      }));
    }
  };


  // Ciclo di animazione continuo con requestAnimationFrame
  const animationFrameId = useRef(null);
  useEffect(() => {
    const update = () => {
      // Salta l'aggiornamento se il gioco è in pausa o finito
      if (gameState.isGameOver) {
        return;
      }

      setBall(prev => {
        let { x, y, vx, vy, isOnGround, isJumping } = prev;
        const currentPressedButtons = pressedButtonsRef.current || {};

        // Gestione dell'accelerazione orizzontale
        if (currentPressedButtons[BUTTONS.LEFT]) {
          vx -= horizontalAcceleration;
        } 
        if (currentPressedButtons[BUTTONS.RIGHT]) {
          vx += horizontalAcceleration;
        } 

        // Se non ci sono input orizzontali, applica attrito
        if (!currentPressedButtons[BUTTONS.LEFT] && !currentPressedButtons[BUTTONS.RIGHT]) {
          // Applica attrito per rallentare gradualmente
          if (vx > 0) {
            vx = Math.max(vx - friction, 0);
          } else if (vx < 0) {
            vx = Math.min(vx + friction, 0);
          }
        }

        // Limita la velocità orizzontale
        if (vx > maxHorizontalSpeed) vx = maxHorizontalSpeed;
        if (vx < -maxHorizontalSpeed) vx = -maxHorizontalSpeed;

        // Gestione per il pulsante A (salto)
        if (currentPressedButtons[BUTTONS.A] && isOnGround) {
          vy = jumpVelocity;
          isOnGround = false;
          isJumping = true;
        } else if (!currentPressedButtons[BUTTONS.A]) {
          isJumping = false;
        }

        // Applica la gravità alla velocità verticale
        vy += gravity;

        // Aggiorna le posizioni
        const newX = x + vx;
        const newY = y + vy;

        // Controlla collisioni con le piattaforme
        const platformCollision = checkPlatformCollision(newX, newY, vx, vy);

        if (platformCollision.collision) {
          // Aggiorna posizione e velocità in base alla collisione
          x = platformCollision.x !== undefined ? platformCollision.x : newX;
          y = platformCollision.y !== undefined ? platformCollision.y : newY;
          vx = platformCollision.vx !== undefined ? platformCollision.vx : vx;
          vy = platformCollision.vy !== undefined ? platformCollision.vy : vy;
          isOnGround = platformCollision.isOnGround !== undefined ? platformCollision.isOnGround : isOnGround;
        } else {
          // Nessuna collisione, aggiorna normalmente
          x = newX;
          y = newY;
          isOnGround = false;
        }

        // Controlla se è stato raccolto un oggetto collezionabile
        checkCollectableCollision(x, y);

        // Controlla collisioni con nemici o punte
        if (checkHazardCollision(x, y)) {
          playerDeath();
        }

        // Controlla se il giocatore ha raggiunto il traguardo
        if (checkFinishCollision(x, y)) {
          completeLevel();
        }

        // Controllo se il giocatore è caduto dal livello
        if (y < -10) {
          playerDeath();
          return { x, y: 0, vx: 0, vy: 0, isOnGround: false, isJumping: false };
        }

        // Clamping della posizione orizzontale nel container
        if (x < 0) {
          x = 0;
          vx = 0;
        }
        if (x > 100 - ballSize) {
          x = 100 - ballSize;
          vx = 0;
        }

        // Gestione dei pulsanti START e SELECT
        if (currentPressedButtons[BUTTONS.START]) {
          // Reimposta il livello
          if (GAME_LEVELS[currentLevel]) {
            const { startPosition } = GAME_LEVELS[currentLevel];
            return {
              x: startPosition.x,
              y: startPosition.y,
              vx: 0,
              vy: 0,
              isOnGround: false,
              isJumping: false
            };
          }
        }

        if (currentPressedButtons[BUTTONS.SELECT]) {
          // Cambia il livello
          setCurrentLevel(prev => (prev + 1) % GAME_LEVELS.length);
        }

        // Supporto per la pressione DOWN (abbassarsi o accelerare la caduta)
        if (currentPressedButtons[BUTTONS.DOWN] && !isOnGround) {
          vy -= 0.5; // Accelera la caduta
        }

        return { x, y, vx, vy, isOnGround, isJumping };
      });

      animationFrameId.current = requestAnimationFrame(update);
    };

    animationFrameId.current = requestAnimationFrame(update);
    return () => cancelAnimationFrame(animationFrameId.current);
  }, [currentLevel, gameState.isGameOver]);

  // Renderizza il livello corrente
  const renderLevel = () => {
    if (!GAME_LEVELS[currentLevel]) return null;

    const level = GAME_LEVELS[currentLevel];

    return (
      <>
        {level.elements.map((element, index) => {
          // Salta se la piattaforma è scomparsa
          if (element.type === LEVEL_TYPES.DISAPPEARING && disappearingPlatforms[index]) {
            return null;
          }

          // Salta se l'oggetto collezionabile è stato raccolto
          if (element.type === LEVEL_TYPES.COLLECTABLE && collectedItems.includes(index)) {
            return null;
          }

          // Calcola la posizione per elementi in movimento
          let elementX = element.x;
          let elementY = element.y;

          if (element.type === LEVEL_TYPES.MOVING) {
            const time = Date.now() / 1000;
            const cycle = Math.sin(time * element.speed);
            const offset = element.offset || 0;

            elementX = element.x + element.moveX * cycle;
            elementY = element.y + element.moveY * cycle;
          }

          // Renderizza in base al tipo
          switch(element.type) {
            case LEVEL_TYPES.PLATFORM:
              return (
                <div
                  key={index}
                  className="platform"
                  style={{
                    left: `${elementX}%`,
                    bottom: `${elementY}%`,
                    width: `${element.width}%`,
                    height: `${element.height}%`
                  }}
                />
              );
            case LEVEL_TYPES.MOVING:
              return (
                <div
                  key={index}
                  className="platform moving"
                  style={{
                    left: `${elementX}%`,
                    bottom: `${elementY}%`,
                    width: `${element.width}%`,
                    height: `${element.height}%`
                  }}
                />
              );
            case LEVEL_TYPES.BOUNCE:
              return (
                <div
                  key={index}
                  className="platform bounce"
                  style={{
                    left: `${elementX}%`,
                    bottom: `${elementY}%`,
                    width: `${element.width}%`,
                    height: `${element.height}%`
                  }}
                />
              );
            case LEVEL_TYPES.DISAPPEARING:
              return (
                <div
                  key={index}
                  className="platform disappearing"
                  style={{
                    left: `${elementX}%`,
                    bottom: `${elementY}%`,
                    width: `${element.width}%`,
                    height: `${element.height}%`
                  }}
                />
              );
            case LEVEL_TYPES.ENEMY:
              return (
                <div
                  key={index}
                  className="enemy"
                  style={{
                    left: `${elementX}%`,
                    bottom: `${elementY}%`,
                    width: `${element.width}%`,
                    height: `${element.height}%`
                  }}
                />
              );
            case LEVEL_TYPES.SPIKE:
              return (
                <div
                  key={index}
                  className="spike"
                  style={{
                    left: `${elementX}%`,
                    bottom: `${elementY}%`,
                    width: `${element.width}%`,
                    height: `${element.height}%`
                  }}
                />
              );
            case LEVEL_TYPES.COLLECTABLE:
              return (
                <div
                  key={index}
                  className="collectable"
                  style={{
                    left: `${elementX}%`,
                    bottom: `${elementY}%`,
                    width: "5%",
                    height: "5%"
                  }}
                />
              );
            case LEVEL_TYPES.FINISH:
              return (
                <div
                  key={index}
                  className="finish"
                  style={{
                    left: `${elementX}%`,
                    bottom: `${elementY}%`,
                    width: "10%",
                    height: "10%"
                  }}
                />
              );
            default:
              return null;
          }
        })}
      </>
    );
  };

  // Renderizza l'interfaccia di gioco
  const renderGameInterface = () => {
    if (gameState.isGameOver) {
      return (
        <div className="game-over">
          <h2>{gameState.isLevelComplete ? "Congratulazioni!" : "Game Over"}</h2>
          <p>Punteggio: {gameState.score}</p>
          <p>Oggetti raccolti: {gameState.collectables}</p>
          <button 
            onClick={() => {
              setCurrentLevel(0);
              setGameState({
                lives: 3,
                score: 0,
                collectables: 0,
                isGameOver: false,
                isLevelComplete: false
              });
            }}
          >
            Ricomincia
          </button>
        </div>
      );
    }

    const renderLives = () => {
      const livesArray = [];
      for (let i = 0; i < gameState.lives; i++) {
        livesArray.push(<span key={i} className="life-icon"></span>);
      }
      return livesArray;
    };

    return (
      <>
        <div className="game-info">
          <div>Punti: {gameState.score}</div>
          <div className="lives-display">
            Vite: {renderLives()}
          </div>
        </div>
      </>
    );
  };

  return (
    <div className="emu-container" style={{ backgroundColor: GAME_LEVELS[currentLevel]?.backgroundColor || "#87CEEB" }}>
      {renderLevel()}

      <div
        className={('ball')}
        style={{
          left: `${ball.x}%`,
          bottom: `${ball.y}%`,
          width: `${ballSize}%`,
          height: `${ballSize}%`
        }}
      ></div>

      {renderGameInterface()}
    </div>
  );
}

export default EmuSchermo;
