import React, { useState } from "react";

const BUTTONS = {
  A: 1,
  B: 2,
  START: 3,
  SELECT: 4,
  UP: 5,
  DOWN: 6,
  LEFT: 7,
  RIGHT: 8,
};

function EmuSchermo({ button }) {
  const [lastBut, setLastBut] = useState("");

  // If you want to update state based on a button press,
  // you could call getButtons like: getButtons(button);
  return <div className="win">{lastBut}</div>;
}

function getButtons(button) {
  switch (button) {
    case BUTTONS.A:
      // Set state or perform actions as needed
      console.log("A pressed");
      break;
    // Handle other cases...
    default:
      console.warn("Input non riconosciuto");
  }
}

// Use named exports for multiple components/helpers:
export { EmuSchermo, getButtons };
