import React from "react";
import EmuSchermo from "./EmuSchermo";
import "./HomeP.css";

function HomeP() {
  return (
    <div className="emuContainer">
      {/* Schermo */}
      <div className="screenContainer">
        <div className="windowEmu">
          <EmuSchermo />
        </div>
      </div>

      {/* Controlli */}
      <div className="controlsContainer">
        <div className="controlButtons">
          {/* D-pad */}
          <div className="dpad">
            <button className="arrow up"></button>
            <button className="arrow left"></button>
            <button className="arrow right"></button>
            <button className="arrow down"></button>
          </div>

          {/* Pulsanti A/B */}
          <div className="actionButtons">
            <button className="red-button">A</button>
            <button className="blue-button">B</button>
          </div>
        </div>

        {/* SELECT/START */}
        <div className="startSelectContainer">
          <button className="select-button">SELECT</button>
          <button className="start-button">START</button>
        </div>
      </div>
    </div>
  );
}

export default HomeP;
