import React, { useState, useEffect } from "react";
import "./EmuSchermo.css";

const BUTTONS = {
  A: 1,
  B: 2,
  START: 3,
  SELECT: 4,
  UP: 5,
  DOWN: 6,
  LEFT: 7,
  RIGHT: 8,
};

function EmuSchermo({ button }) {
  const [lastBut, setLastBut] = useState(0);




  useEffect(() => {
    if (button) {
      switch (button) {
        case BUTTONS.A:
          setLastBut(1);
          break;
        case BUTTONS.B:
          setLastBut(2);
          break;
        case BUTTONS.START:
          setLastBut(3);
          break;
        case BUTTONS.SELECT:
          setLastBut(4);
          break;
        case BUTTONS.UP:
          setLastBut(5);
          break;
        case BUTTONS.DOWN:
          setLastBut(6);
          break;
        case BUTTONS.LEFT:
          setLastBut(7);
          break;
        case BUTTONS.RIGHT:
          setLastBut(8);
          break;
        default:
          console.warn(-1);
        break;
      }
    }
  }, [button]);

  return (
    <div className="win">
        {lastBut}
    </div>
    );
}

export default EmuSchermo;
