// EmuSchermo.jsx
import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';

const EmuSchermo = ({ inputQueue }) => {

    const [val, setQueue] = useState([])



  useEffect(() => {
    const processInputs = async () => {
      if (inputQueue.length > 0) {
        // Simula il processing degli input
        const lastInput = inputQueue[inputQueue.length - 1];
        console.log('Elaboro input:', lastInput);

        setQueue(lastInput);
      }
    };

    processInputs();
  }, [inputQueue]);

  return (
    <div className="windowEmu">
      {/* Schermo dell'emulatore */}
      <div className="screen-content">
        <div className="input-overlay">
          {inputQueue.join(', ')}
        </div>
      </div>
    </div>
  );
};

EmuSchermo.propTypes = {
  inputQueue: PropTypes.arrayOf(PropTypes.string).isRequired
};

export default EmuSchermo;
