import React, { useState } from 'react';
import './App.css';
import GameP from './components/GameP';
import HomeScreen from './components/HomeScreen';

function App() {
  // Stato per gestire l'avvio del gioco: se false si vede la Home, se true viene caricato il gioco
  const [gameStarted, setGameStarted] = useState(false);

  // Funzione che viene chiamata quando viene premuto il pulsante Play
  const handlePlay = () => {
    setGameStarted(true);
  };

  return (
    <div className="appContainer">
      {gameStarted ? <GameP /> : <HomeScreen onPlay={handlePlay} />}
    </div>
  );
}

export default App;
