// EmuSchermo.jsx
import React, { useEffect, useState, useRef, useCallback } from 'react';
import PropTypes from 'prop-types';

const PHYSICS_CONSTANTS = {
  GRAVITY: 0.5,
  JUMP_FORCE: -12,
  MOVE_SPEED: 5,
  BALL_SIZE: 30
};

const EmuSchermo = ({ inputQueue }) => {
  const screenRef = useRef(null);
  const [position, setPosition] = useState({ x: 0, y: 0 });
  const [velocity, setVelocity] = useState({ dx: 0, dy: 0 });
  const [isGrounded, setIsGrounded] = useState(false);
  const [screenSize, setScreenSize] = useState({ width: 0, height: 0 });

  const updateSize = useCallback(() => {
    if(screenRef.current) {
      const rect = screenRef.current.getBoundingClientRect();
      setScreenSize({
        width: rect.width,
        height: rect.height
      });
      // Imposta la posizione iniziale al centro
      setPosition({
        x: (rect.width - PHYSICS_CONSTANTS.BALL_SIZE) / 2,
        y: (rect.height - PHYSICS_CONSTANTS.BALL_SIZE) / 2
      });
    }
  }, []);

  useEffect(() => {
    updateSize();
    window.addEventListener('resize', updateSize);
    return () => window.removeEventListener('resize', updateSize);
  }, [updateSize]);

  const handleInput = useCallback(() => {
    setVelocity(prev => {
      const { MOVE_SPEED, JUMP_FORCE, GRAVITY } = PHYSICS_CONSTANTS;
      let newDx = 0;

      if(inputQueue.includes('LEFT')) newDx = -MOVE_SPEED;
      if(inputQueue.includes('RIGHT')) newDx = MOVE_SPEED;

      let newDy = prev.dy;
      if(inputQueue.includes('UP') && isGrounded) {
        newDy = JUMP_FORCE;
        setIsGrounded(false);
      }

      return { 
        dx: newDx, 
        dy: newDy + (!isGrounded ? GRAVITY : 0) 
      };
    });
  }, [inputQueue, isGrounded]);

  useEffect(() => {
    handleInput();
  }, [handleInput]);

  useEffect(() => {
    const { BALL_SIZE } = PHYSICS_CONSTANTS;
    let animationFrameId;

    const updatePosition = () => {
      setPosition(prev => {
        let newX = prev.x + velocity.dx;
        let newY = prev.y + velocity.dy;

        // Boundary checks
        newX = Math.max(0, Math.min(newX, screenSize.width - BALL_SIZE));
        newY = Math.max(0, Math.min(newY, screenSize.height - BALL_SIZE));

        // Ground detection
        const isOnGround = newY >= screenSize.height - BALL_SIZE;
        setIsGrounded(isOnGround);

        return { x: newX, y: isOnGround ? screenSize.height - BALL_SIZE : newY };
      });

      animationFrameId = requestAnimationFrame(updatePosition);
    };

    animationFrameId = requestAnimationFrame(updatePosition);
    return () => cancelAnimationFrame(animationFrameId);
  }, [velocity, screenSize]);

  return (
    <div 
      className="windowEmu" 
      ref={screenRef}
      style={{
        width: '100%',
        height: '100%',
        position: 'relative',
        overflow: 'hidden',
        backgroundColor: '#9acd32'
      }}
    >
      <div
        className="ball"
        style={{
          position: 'absolute',
          left: position.x,
          top: position.y,
          width: PHYSICS_CONSTANTS.BALL_SIZE,
          height: PHYSICS_CONSTANTS.BALL_SIZE,
          borderRadius: '50%',
          backgroundColor: 'red',
          transition: 'left 0.1s linear, top 0.1s ease-out',
          boxShadow: '0 4px 8px rgba(0,0,0,0.2)'
        }}
      />
    </div>
  );
};

EmuSchermo.propTypes = {
  inputQueue: PropTypes.arrayOf(PropTypes.string).isRequired
};

export default EmuSchermo;
