import React, { useState, useEffect } from "react";
import "./App.css";
import HomeScreen from "./components/HomeScreen";
import Profile from "./components/Profile";
import EmuSchermo from "./components/EmuSchermo"; // Componente del gioco
import UsernameModal from "./components/UsernameModal";

function App() {
  // Stato per gestire la schermata corrente: "home", "game", "profile", ecc.
  const [screen, setScreen] = useState("home");
  // Stato per il nome utente: se non esiste in localStorage, rimane una stringa vuota
  const [username, setUsername] = useState(localStorage.getItem("username") || "");

  // Stato per visualizzare il modal se non è presente un nome utente
  const [showModal, setShowModal] = useState(false);

  // Al montaggio verifica se esiste già il nome utente
  useEffect(() => {
    if (!username) {
      setShowModal(true);
    }
  }, [username]);

  const handlePlay = () => {
    setScreen("game");
  };

  const handleProfile = () => {
    setScreen("profile");
  };

  const handleHome = () => {
    setScreen("home");
  };

  const handleSaveUsername = (newUsername) => {
    setUsername(newUsername);
    localStorage.setItem("username", newUsername);
  };

  return (
    <>
      {showModal && (
        <UsernameModal 
          onSave={(name) => {
            handleSaveUsername(name);
            setShowModal(false);
          }}
        />
      )}

      {screen === "home" && <HomeScreen onPlay={handlePlay} onProfile={handleProfile} />}
      {screen === "profile" && (
        <Profile 
          username={username} 
          onSave={handleSaveUsername} 
          onBack={handleHome} 
        />
      )}
      {screen === "game" && <EmuSchermo onGoHome={handleHome} />}
    </>
  );
}

export default App;
