import React, { useState } from "react";





const BUTTONS = {
    A: 1,
    B: 2,
    START: 3,
    SELECT: 4,
    UP: 5,
    DOWN: 6,
    LEFT: 7,
    RIGHT: 8
  };

function EmuSchermo(){

    function getButtons(button){
        switch(button) {
            case BUTTONS.A:
              setLastBut("A pressed");
            break;
            case BUTTONS.B:
              setLastBut("B pressed");
              break;
            case BUTTONS.START:
              setLastBut("Start pressed");
              break;
            case BUTTONS.SELECT:
              setLastBut("Select pressed");
              break;
            case BUTTONS.UP:
              setLastBut("Up pressed");
              break;
            case BUTTONS.DOWN:
              setLastBut("Down pressed");
              break;
            case BUTTONS.LEFT:
              setLastBut("Left pressed");
              break;
            case BUTTONS.RIGHT:
              setLastBut("Right pressed");
              break;
            default:
              console.warn("Input non riconosciuto");
              break;
          }

    }
    const [LastBut, setLastBut] = useState("");

    return (
        <div className="win">
            {LastBut}
        </div>
    );
}
export default EmuSchermo;
