import React, { useState, useEffect, useCallback } from 'react';
import './SlotTime.css';

// Costanti per i pulsanti del controller
const BUTTONS = {
  A: 1,
  B: 2,
  START: 3,
  SELECT: 4,
  LEFT: 7,
  RIGHT: 8,
};

// Configurazione delle impostazioni di gioco
const GAME_CONFIG = {
  ANIMATION_DURATION: 2000,
  MULTIPLIERS: [0, 1, 2, 3],
  MIN_BET: 1,
};

// Componenti UI secondari
const SlotHeader = () => (
  <div className="slot-header">
    <h2 className="pixelated">Slot Game</h2>
  </div>
);

const BetControls = ({ selectedBet, maxBet }) => (
  <div className="bet-controls pixelated">
    <p>Tempo residuo (monete): {maxBet}</p>
    <div className="bet-selector">
      <span className="bet-label">Puntata selezionata:</span>
      <span className="bet-value">{selectedBet}</span>
    </div>
  </div>
);

const Instructions = () => (
  <div className="instructions pixelated">
    <p>Usa <span className="button-hint">â/â</span> per modificare la puntata</p>
    <p>Premi <span className="button-hint">A</span> per confermare</p>
  </div>
);

const AnimationDisplay = () => (
  <div className="animation-display">
    <div className="slot-reels">
      <div className="reel"></div>
      <div className="reel"></div>
      <div className="reel"></div>
    </div>
    <p className="animation-text pixelated">Rotazione in corso...</p>
  </div>
);

const ResultDisplay = ({ result }) => (
  <div className={`result-display ${result.multiplier > 0 ? 'win' : 'lose'} pixelated`}>
    {result.multiplier > 0 ? (
      <>
        <h3 className="win-title">Hai vinto!</h3>
        <p className="win-details">
          <span className="multiplier">Ã{result.multiplier}</span>
          <span className="winnings">{result.winnings} monete</span>
        </p>
      </>
    ) : (
      <h3 className="lose-title">Non hai vinto questa volta</h3>
    )}
    <p className="continue-hint">Tocca per continuare...</p>
  </div>
);

function SlotTime({ timeSlot, onSlotComplete, pressedButtons }) {
  const [selectedBet, setSelectedBet] = useState(1);
  const [animating, setAnimating] = useState(false);
  const [result, setResult] = useState(null);
  const [gameState, setGameState] = useState('betting'); // 'betting', 'spinning', 'result'

  // Tracciamento dello stato precedente dei pulsanti per rilevare i cambiamenti
  const [prevPressedButtons, setPrevPressedButtons] = useState({});

  // Imposta la scommessa iniziale a 1 o al valore massimo disponibile
  useEffect(() => {
    setSelectedBet(prev => Math.min(Math.max(1, prev), timeSlot));
  }, [timeSlot]);

  // Calcola un risultato casuale
  const calculateResult = useCallback(() => {
    const multiplier = GAME_CONFIG.MULTIPLIERS[
      Math.floor(Math.random() * GAME_CONFIG.MULTIPLIERS.length)
    ];
    return {
      multiplier,
      winnings: selectedBet * multiplier
    };
  }, [selectedBet]);

  // Gestisci l'animazione e il calcolo del risultato
  const handleSpin = useCallback(() => {
    if (selectedBet <= 0 || selectedBet > timeSlot) return;

    setAnimating(true);
    setGameState('spinning');

    setTimeout(() => {
      const newResult = calculateResult();
      setResult(newResult);
      setAnimating(false);
      setGameState('result');
    }, GAME_CONFIG.ANIMATION_DURATION);
  }, [calculateResult, selectedBet, timeSlot]);

  // Gestione diretta dei pulsanti sullo schermo
  const handleLeftButtonClick = () => {
    if (gameState === 'betting') {
      setSelectedBet(prev => Math.max(prev - 1, GAME_CONFIG.MIN_BET));
    }
  };

  const handleRightButtonClick = () => {
    if (gameState === 'betting') {
      setSelectedBet(prev => Math.min(prev + 1, timeSlot));
    }
  };

  const handleAButtonClick = () => {
    if (gameState === 'betting') {
      handleSpin();
    }
  };

  // Gestisci gli input dei pulsanti esterni (dal controller)
  useEffect(() => {
    if (gameState !== 'betting' || !pressedButtons) return;

    // Controlla se un pulsante Ã¨ stato appena premuto rispetto allo stato precedente
    const isButtonJustPressed = (buttonCode) => {
      return pressedButtons[buttonCode] && !prevPressedButtons[buttonCode];
    };

    if (isButtonJustPressed(BUTTONS.LEFT)) {
      setSelectedBet(prev => Math.max(prev - 1, GAME_CONFIG.MIN_BET));
    } else if (isButtonJustPressed(BUTTONS.RIGHT)) {
      setSelectedBet(prev => Math.min(prev + 1, timeSlot));
    } else if (isButtonJustPressed(BUTTONS.A)) {
      handleSpin();
    }

    // Aggiorna lo stato precedente dei pulsanti
    setPrevPressedButtons(pressedButtons);
  }, [pressedButtons, prevPressedButtons, gameState, timeSlot, handleSpin]);

  // Gestisci il click per completare il gioco
  const handleClick = () => {
    if (gameState === 'result') {
      onSlotComplete(result.winnings);
    }
  };

  return (
    <div className={`slot-overlay game-state-${gameState}`} onClick={handleClick}>
      <div className={`slot-container ${animating ? 'animating' : ''}`}>
        <SlotHeader />

        <BetControls selectedBet={selectedBet} maxBet={timeSlot} />

        {gameState === 'betting' && <Instructions />}

        {gameState === 'spinning' && <AnimationDisplay />}

        {gameState === 'result' && <ResultDisplay result={result} />}

        {/* Pulsanti sullo schermo con gestori di eventi corretti */}
        {gameState === 'betting' && (
          <div className="on-screen-controls">
            <button 
              className="control-button left-button" 
              onClick={handleLeftButtonClick} 
              aria-label="Diminuisci puntata"
            >
              â
            </button>
            <button 
              className="control-button right-button" 
              onClick={handleRightButtonClick} 
              aria-label="Aumenta puntata"
            >
              â
            </button>
            <button 
              className="control-button a-button" 
              onClick={handleAButtonClick} 
              aria-label="Conferma"
            >
              A
            </button>
          </div>
        )}
      </div>
    </div>
  );
}

export default SlotTime;
