import React, { useState, useEffect, useRef } from "react";
import "./EmuSchermo.css";

const BUTTONS = {
  A: 1,
  B: 2,
  START: 3,
  SELECT: 4,
  LEFT: 7,
  RIGHT: 8,
};       

function createSeededRandom(seed) {
    const a = 1664525;
    const c = 1013904223;
    const m = 2**32;
    let state = seed;

    return function() {
        state = (a * state + c) % m;
        return state / m;
    };
}

function generateLevel(seed) {
    const rand = createSeededRandom(seed);
    const platforms = [];
    let currentHeight = 20;
    const maxX = 100;

    const platformConfig = {
        typeChance: { p: 0.7, s: 0.3 },     
        heightRange: [3, 20],               
        widthRange: [10, 40],             
        spacing: [10, 60] 
    };

    // Scegli un indice casuale per la piattaforma T
    const tIndex = Math.floor(rand() * 4);

    for (let i = 0; i < 4; i++) {
        // Determina il tipo
        let type;
        if (i === tIndex) {
            type = "T"; // Piattaforma speciale
        } else {
            // Assegna normali piattaforme con probabilità ridotte
            const typeRand = rand();
            type = typeRand < platformConfig.typeChance.s ? 's' : 'p';
        }

        // Altezza
        if (i > 0) {
            const heightIncrement = Math.floor(
                rand() * (platformConfig.heightRange[1] - platformConfig.heightRange[0] + 1)
            ) + platformConfig.heightRange[0];
            currentHeight += heightIncrement;
        }

        // Larghezza e posizione
        const width = Math.floor(
            rand() * (platformConfig.widthRange[1] - platformConfig.widthRange[0] + 1)
        ) + platformConfig.widthRange[0];

        const posStartX = Math.floor(rand() * (maxX - width));
        const posEndX = posStartX + width;

        platforms.push({
            type: type,
            height: currentHeight,
            posStartX: posStartX,
            posEndX: posEndX
        });
    }

    return platforms;
}

const LEVEL = generateLevel(1);

