const maxX = 100;        // Larghezza massima orizzontale
const minWidth = 10;
const maxWidth = 30;
const minVertical = 5;
const maxVertical = 20;
const marginSafety = 1;  // Margine di sicurezza orizzontale per il salto

// Parametri per la generazione pseudo-casuale
