      direction = -1;
      posStartX = prev.posStartX - gap - minWidth;
    } else {
      posStartX = Math.max(0, maxX - minWidth);
    }
  } else {
    if (prev.posStartX - gap - minWidth >= 0) {
      posStartX = prev.posStartX - gap - minWidth;
    } else if (prev.posEndX + gap + minWidth <= maxX) {
