    Math.floor(rand() * (maxWidth - minWidth + 1)) + minWidth,
    maxX - posStartX
  );
  const posEndX = posStartX + width;

  // La piattaforma finale è il traguardo "T"
  platforms.push({
    type: "T",
    height: newHeight,
    posStartX,
    posEndX
  });

  return platforms;
}



