            newY <= platformTop &&
            (newY + ballSize) >= platformBottom
          );

          // 3. Determinazione direzione collisione
          if (horizontalOverlap && verticalOverlap) {
            const prevBottom = prev.y + ballSize;
            if (prevBottom <= platformBottom && vy > 0) {
              // Collisione dal basso (salto contro piattaforma)
              newY = platformBottom - ballSize;
              vy = 0;
            } else if (vy <= 0) {
              // Collisione dall'alto (atterraggio su piattaforma)
              newY = platformTop;
              vy = 0;
            }
            collided = true;
          }
        });

        // Collisione con il terreno solo se non abbiamo colliso con piattaforme
        if (!collided && newY < 0) {
          newY = 0;
          vy = 0;
        }

        // Clamping orizzontale
        newX = Math.max(0, Math.min(newX, 100 - ballSize));

        function handlePlatformCollision(platform) {
          // Calcola le coordinate della piattaforma (o "spike")
          const left = platform.posStartX;
          const right = platform.posEndX;
          const bottom = platform.height;
          const top = platform.height + 3; // altezza extra per la collisione

          // Verifica collisione orizzontale e verticale
          const horizontalCollision = (x + ballSize > left && x < right);
          const verticalCollision = (y < top && y + ballSize > bottom && vy <= 0);

          if (horizontalCollision && verticalCollision) {
            // Resetta la posizione e le velocità
            newX = 2;
            newY = 0;
            vx = 0;
            vy = 0;

            if (platform.type === "s") {
              // Quando si tocca una spike, la piattaforma diventa "p" e si genera un nuovo livello
              platform.type = "p";
              //SETLEVEL(generateLevel(level));
            } else if (platform.type === "T") {
              // Quando si raggiunge la piattaforma traguardo, si passa al livello successivo
              const newLevel = level + 1;
              setLevel(newLevel);
              SETLEVEL(generateLevel(newLevel));
            }
            return true; // Collisione gestita
          }
          return false;
        }

        // Nel ciclo principale di aggiornamento:
        for (const platform of platformsRef.current) {
          if (platform.type === "s" || platform.type === "T") {
            if (handlePlatformCollision(platform)) {
              // Uscita immediata se una collisione è stata gestita
              break;
            }
          }
        }

        return { x: newX, y: newY, vx, vy };
      });
      animationFrameId.current = requestAnimationFrame(update);
    };

    animationFrameId.current = requestAnimationFrame(update);
    return () => cancelAnimationFrame(animationFrameId.current);
  }, [level, ballSize, horizontalAcceleration, friction, maxHorizontalSpeed, jumpVelocity, gravity]);

  return (
    <div className="emu-container">
      <div
        className="ball"
        style={{
          left: `${ball.x}%`,
          bottom: `${ball.y}%`,
          width: `${ballSize}%`,
          height: `${ballSize + 2}%`,
        }}
      ></div>

      {LEVEL.map((item, i) => {
        if (item.type === "T") {
          return (
            <div
              key={i}
              className="t-platform"
              style={{
                left: `${item.posStartX}%`,
                width: `${item.posEndX - item.posStartX}%`,
                bottom: `${item.height}%`,
                height: "2%",
              }}
            />
          );
        } else if (item.type === "p") {
          return (
            <div
              key={i}
              className="platform"
              style={{
                left: `${item.posStartX}%`,
                width: `${item.posEndX - item.posStartX}%`,
                bottom: `${item.height}%`,
                height: "2%",
              }}
            />
          );
        } else {
          return (
            <div
              key={i}
              className="spike"
              style={{
                left: `${item.posStartX}%`,
                width: `${item.posEndX - item.posStartX}%`,
                bottom: `${item.height}%`,
                height: "2%",
              }}
            />
          );
        }
      })}
    </div>
  );
}

export default EmuSchermo;
