import React, { useState, useEffect, useRef } from "react";
import "./EmuSchermo.css";

// Costanti per la fisica della pallina
const horizontalAcceleration = 0.2;
const maxHorizontalSpeed = 2;
const friction = 0.1;
const jumpVelocity = 7;
const gravity = -0.5;
const BALL_SIZE = 5;

// Array di livelli: ogni livello ha le sue piattaforme e spike
const levels = [
  [
    { id: 1, x: 10, y: 30, width: 20, height: 5, dx: 0.15, type: "platform" },
    { id: 2, x: 60, y: 50, width: 20, height: 5, dx: -0.2, type: "platform" },
    { id: 3, x: 40, y: 70, width: 15, height: 5, dx: 0.1, type: "spike" }
  ],
  [
    { id: 4, x: 20, y: 25, width: 25, height: 5, dx: 0.2, type: "platform" },
    { id: 5, x: 50, y: 50, width: 15, height: 5, dx: -0.15, type: "spike" },
    { id: 6, x: 70, y: 75, width: 20, height: 5, dx: 0.1, type: "platform" }
  ],
  [
    { id: 7, x: 15, y: 40, width: 20, height: 5, dx: -0.1, type: "platform" },
    { id: 8, x: 60, y: 60, width: 20, height: 5, dx: 0.15, type: "spike" },
    { id: 9, x: 30, y: 80, width: 20, height: 5, dx: -0.2, type: "platform" }
  ]
];

function EmuSchermo() {
  const [ball, setBall] = useState({ x: 50, y: 0, vx: 0, vy: 0, isOnGround: true });
  const [lives, setLives] = useState(3);
  const [levelIndex, setLevelIndex] = useState(0);
  const [platforms, setPlatforms] = useState(levels[levelIndex]);

  const keysPressedRef = useRef({});
  const animationFrameRef = useRef(null);

  useEffect(() => {
    const handleKeyDown = (e) => { keysPressedRef.current[e.code] = true; };
    const handleKeyUp = (e) => { keysPressedRef.current[e.code] = false; };

    window.addEventListener("keydown", handleKeyDown);
    window.addEventListener("keyup", handleKeyUp);
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
      window.removeEventListener("keyup", handleKeyUp);
    };
  }, []);

  const isColliding = (ballObj, rect) => {
    return (
      ballObj.x < rect.x + rect.width &&
      ballObj.x + BALL_SIZE > rect.x &&
      ballObj.y < rect.y + rect.height &&
      ballObj.y + BALL_SIZE > rect.y
    );
  };

  const resetBall = () => {
    setBall({ x: 50, y: 0, vx: 0, vy: 0, isOnGround: true });
  };

  const nextLevel = () => {
    if (levelIndex < levels.length - 1) {
      setLevelIndex(levelIndex + 1);
      setPlatforms(levels[levelIndex + 1]);
      resetBall();
    } else {
      console.log("Hai completato tutti i livelli!");
    }
  };

  useEffect(() => {
    const update = () => {
      setBall(prev => {
        let { x, y, vx, vy, isOnGround } = prev;
        const keys = keysPressedRef.current;

        if (keys["ArrowLeft"]) vx -= horizontalAcceleration;
        if (keys["ArrowRight"]) vx += horizontalAcceleration;
        if (!keys["ArrowLeft"] && !keys["ArrowRight"]) {
          if (vx > 0) vx = Math.max(vx - friction, 0);
          if (vx < 0) vx = Math.min(vx + friction, 0);
        }

        vx = Math.max(Math.min(vx, maxHorizontalSpeed), -maxHorizontalSpeed);
        if (keys["Space"] && isOnGround) {
          vy = jumpVelocity;
          isOnGround = false;
        }

        vy += gravity;
        x += vx;
        y += vy;

        if (y < 0) {
          y = 0;
          vy = 0;
          isOnGround = true;
        }

        if (x < 0) { x = 0; vx = 0; }
        if (x > 95) { x = 95; vx = 0; }

        platforms.forEach(p => {
          if (isColliding({ x, y }, p)) {
            if (p.type === "platform" && vy <= 0 && y >= p.y + p.height - 2) {
              y = p.y + p.height;
              vy = 0;
              isOnGround = true;
            } else if (p.type === "spike") {
              setLives(prevLives => Math.max(prevLives - 1, 0));
              resetBall();
            }
          }
        });

        if (y > 90) {
          nextLevel();
        }

        return { x, y, vx, vy, isOnGround };
      });

      setPlatforms(prevPlatforms => prevPlatforms.map(p => {
        let newX = p.x + p.dx;
        if (newX < 0 || newX + p.width > 100) {
          p.dx = -p.dx;
          newX = p.x + p.dx;
        }
        return { ...p, x: newX };
      }));

      if (lives <= 0) {
        cancelAnimationFrame(animationFrameRef.current);
        return;
      }

      animationFrameRef.current = requestAnimationFrame(update);
    };

    animationFrameRef.current = requestAnimationFrame(update);
    return () => cancelAnimationFrame(animationFrameRef.current);
  }, [lives, platforms, levelIndex]);

  return (
    <div className="emu-container">
      <div className="lives-display">Vite: {lives} | Livello: {levelIndex + 1}</div>
      <div className="ball" style={{ left: `${ball.x}%`, bottom: `${ball.y}%` }} />
      {platforms.map(p => (
        <div
          key={p.id}
          className={p.type === "platform" ? "platform" : "spike"}
          style={{ left: `${p.x}%`, bottom: `${p.y}%`, width: `${p.width}%`, height: `${p.height}%` }}
        />
      ))}
    </div>
  );
}

export default EmuSchermo;
