import React, { useState, useCallback, useEffect } from "react";
import EmuSchermo from "./EmuSchermo";
import "./HomeP.css";

const BUTTONS = {
  A: "A",
  B: "B",
  START: "START",
  SELECT: "SELECT",
  UP: "UP",
  DOWN: "DOWN",
  LEFT: "LEFT",
  RIGHT: "RIGHT",
};

const initialButtonState = {
  A: false,
  B: false,
  START: false,
  SELECT: false,
  UP: false,
  DOWN: false,
  LEFT: false,
  RIGHT: false,
};

function HomeP() {
  const [buttonsPressed, setButtonsPressed] = useState(initialButtonState);

  // Gestione degli eventi da tastiera
  const handleKeyDown = useCallback((e) => {
    switch (e.key.toLowerCase()) {
      case "a":
        setButtonsPressed(prev => ({ ...prev, A: true }));
        break;
      case "b":
        setButtonsPressed(prev => ({ ...prev, B: true }));
        break;
      case "enter":
        setButtonsPressed(prev => ({ ...prev, START: true }));
        break;
      case "backspace":
        setButtonsPressed(prev => ({ ...prev, SELECT: true }));
        break;
      case "arrowup":
        setButtonsPressed(prev => ({ ...prev, UP: true }));
        break;
      case "arrowdown":
        setButtonsPressed(prev => ({ ...prev, DOWN: true }));
        break;
      case "arrowleft":
        setButtonsPressed(prev => ({ ...prev, LEFT: true }));
        break;
      case "arrowright":
        setButtonsPressed(prev => ({ ...prev, RIGHT: true }));
        break;
      default:
        break;
    }
  }, []);

  const handleKeyUp = useCallback((e) => {
    switch (e.key.toLowerCase()) {
      case "a":
        setButtonsPressed(prev => ({ ...prev, A: false }));
        break;
      case "b":
        setButtonsPressed(prev => ({ ...prev, B: false }));
        break;
      case "enter":
        setButtonsPressed(prev => ({ ...prev, START: false }));
        break;
      case "backspace":
        setButtonsPressed(prev => ({ ...prev, SELECT: false }));
        break;
      case "arrowup":
        setButtonsPressed(prev => ({ ...prev, UP: false }));
        break;
      case "arrowdown":
        setButtonsPressed(prev => ({ ...prev, DOWN: false }));
        break;
      case "arrowleft":
        setButtonsPressed(prev => ({ ...prev, LEFT: false }));
        break;
      case "arrowright":
        setButtonsPressed(prev => ({ ...prev, RIGHT: false }));
        break;
      default:
        break;
    }
  }, []);

  useEffect(() => {
    window.addEventListener("keydown", handleKeyDown);
    window.addEventListener("keyup", handleKeyUp);
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
      window.removeEventListener("keyup", handleKeyUp);
    };
  }, [handleKeyDown, handleKeyUp]);

  // Gestione degli eventi dei pulsanti on-screen (usando onMouseDown/Up e onTouchStart/End per compatibilitÃ  mobile)
  const handleMouseDown = (button) => (e) => {
    e.preventDefault();
    setButtonsPressed(prev => ({ ...prev, [button]: true }));
  };

  const handleMouseUp = (button) => (e) => {
    e.preventDefault();
    setButtonsPressed(prev => ({ ...prev, [button]: false }));
  };

  const handleTouchStart = (button) => (e) => {
    e.preventDefault();
    setButtonsPressed(prev => ({ ...prev, [button]: true }));
  };

  const handleTouchEnd = (button) => (e) => {
    e.preventDefault();
    setButtonsPressed(prev => ({ ...prev, [button]: false }));
  };

  return (
    <div className="emuContainer">
      {/* Portrait Layout */}
      <div className="portraitLayout">
        <div className="screenContainer">
          <div className="windowEmu">
            {/* Passa lo stato di tutti i pulsanti a EmuSchermo */}
            <EmuSchermo buttons={buttonsPressed} />
          </div>
        </div>

        <div className="controlsRow">
          <div className="dpadContainer">
            <div className="dpad">
              <button
                className="arrow up"
                onMouseDown={handleMouseDown(BUTTONS.UP)}
                onMouseUp={handleMouseUp(BUTTONS.UP)}
                onTouchStart={handleTouchStart(BUTTONS.UP)}
                onTouchEnd={handleTouchEnd(BUTTONS.UP)}
                aria-label="Up"
              ></button>
              <button
                className="arrow left"
                onMouseDown={handleMouseDown(BUTTONS.LEFT)}
                onMouseUp={handleMouseUp(BUTTONS.LEFT)}
                onTouchStart={handleTouchStart(BUTTONS.LEFT)}
                onTouchEnd={handleTouchEnd(BUTTONS.LEFT)}
                aria-label="Left"
              ></button>
              <button
                className="arrow right"
                onMouseDown={handleMouseDown(BUTTONS.RIGHT)}
                onMouseUp={handleMouseUp(BUTTONS.RIGHT)}
                onTouchStart={handleTouchStart(BUTTONS.RIGHT)}
                onTouchEnd={handleTouchEnd(BUTTONS.RIGHT)}
                aria-label="Right"
              ></button>
              <button
                className="arrow down"
                onMouseDown={handleMouseDown(BUTTONS.DOWN)}
                onMouseUp={handleMouseUp(BUTTONS.DOWN)}
                onTouchStart={handleTouchStart(BUTTONS.DOWN)}
                onTouchEnd={handleTouchEnd(BUTTONS.DOWN)}
                aria-label="Down"
              ></button>
            </div>
          </div>

          <div className="actionButtonsContainer">
            <div className="actionButtons">
              <button
                className="red-button"
                onMouseDown={handleMouseDown(BUTTONS.A)}
                onMouseUp={handleMouseUp(BUTTONS.A)}
                onTouchStart={handleTouchStart(BUTTONS.A)}
                onTouchEnd={handleTouchEnd(BUTTONS.A)}
                aria-label="A Button"
              >
                A
              </button>
              <button
                className="blue-button"
                onMouseDown={handleMouseDown(BUTTONS.B)}
                onMouseUp={handleMouseUp(BUTTONS.B)}
                onTouchStart={handleTouchStart(BUTTONS.B)}
                onTouchEnd={handleTouchEnd(BUTTONS.B)}
                aria-label="B Button"
              >
                B
              </button>
            </div>
          </div>
        </div>

        <div className="startSelectContainer">
          <button
            className="select-button"
            onMouseDown={handleMouseDown(BUTTONS.SELECT)}
            onMouseUp={handleMouseUp(BUTTONS.SELECT)}
            onTouchStart={handleTouchStart(BUTTONS.SELECT)}
            onTouchEnd={handleTouchEnd(BUTTONS.SELECT)}
          >
            SELECT
          </button>
          <button
            className="start-button"
            onMouseDown={handleMouseDown(BUTTONS.START)}
            onMouseUp={handleMouseUp(BUTTONS.START)}
            onTouchStart={handleTouchStart(BUTTONS.START)}
            onTouchEnd={handleTouchEnd(BUTTONS.START)}
          >
            START
          </button>
        </div>
      </div>

      {/* Landscape Layout */}
      <div className="landscapeLayout">
        <div className="controlsRowLandscape">
          <div className="dpadContainer">
            <div className="dpad">
              <button
                className="arrow up"
                onMouseDown={handleMouseDown(BUTTONS.UP)}
                onMouseUp={handleMouseUp(BUTTONS.UP)}
                onTouchStart={handleTouchStart(BUTTONS.UP)}
                onTouchEnd={handleTouchEnd(BUTTONS.UP)}
                aria-label="Up"
              ></button>
              <button
                className="arrow left"
                onMouseDown={handleMouseDown(BUTTONS.LEFT)}
                onMouseUp={handleMouseUp(BUTTONS.LEFT)}
                onTouchStart={handleTouchStart(BUTTONS.LEFT)}
                onTouchEnd={handleTouchEnd(BUTTONS.LEFT)}
                aria-label="Left"
              ></button>
              <button
                className="arrow right"
                onMouseDown={handleMouseDown(BUTTONS.RIGHT)}
                onMouseUp={handleMouseUp(BUTTONS.RIGHT)}
                onTouchStart={handleTouchStart(BUTTONS.RIGHT)}
                onTouchEnd={handleTouchEnd(BUTTONS.RIGHT)}
                aria-label="Right"
              ></button>
              <button
                className="arrow down"
                onMouseDown={handleMouseDown(BUTTONS.DOWN)}
                onMouseUp={handleMouseUp(BUTTONS.DOWN)}
                onTouchStart={handleTouchStart(BUTTONS.DOWN)}
                onTouchEnd={handleTouchEnd(BUTTONS.DOWN)}
                aria-label="Down"
              ></button>
            </div>
          </div>

          <div className="screenContainer">
            <div className="windowEmu">
              <EmuSchermo buttons={buttonsPressed} />
            </div>
          </div>

          <div className="actionButtonsContainer">
            <div className="actionButtons">
              <button
                className="red-button"
                onMouseDown={handleMouseDown(BUTTONS.A)}
                onMouseUp={handleMouseUp(BUTTONS.A)}
                onTouchStart={handleTouchStart(BUTTONS.A)}
                onTouchEnd={handleTouchEnd(BUTTONS.A)}
                aria-label="A Button"
              >
                A
              </button>
              <button
                className="blue-button"
                onMouseDown={handleMouseDown(BUTTONS.B)}
                onMouseUp={handleMouseUp(BUTTONS.B)}
                onTouchStart={handleTouchStart(BUTTONS.B)}
                onTouchEnd={handleTouchEnd(BUTTONS.B)}
                aria-label="B Button"
              >
                B
              </button>
            </div>
          </div>
        </div>

        <div className="startSelectContainer">
          <button
            className="select-button"
            onMouseDown={handleMouseDown(BUTTONS.SELECT)}
            onMouseUp={handleMouseUp(BUTTONS.SELECT)}
            onTouchStart={handleTouchStart(BUTTONS.SELECT)}
            onTouchEnd={handleTouchEnd(BUTTONS.SELECT)}
          >
            SELECT
          </button>
          <button
            className="start-button"
            onMouseDown={handleMouseDown(BUTTONS.START)}
            onMouseUp={handleMouseUp(BUTTONS.START)}
            onTouchStart={handleTouchStart(BUTTONS.START)}
            onTouchEnd={handleTouchEnd(BUTTONS.START)}
          >
            START
          </button>
        </div>
      </div>
    </div>
  );
}

export default HomeP;
