import React, { useState, useEffect } from 'react';
import './SlotTime.css';

function SlotTime({ timeSlot, onSlotComplete }) {
  const [selectedBet, setSelectedBet] = useState(timeSlot);
  const [animating, setAnimating] = useState(false);

  useEffect(() => {
    const handleKeyDown = (e) => {
      if (animating) return; // Ignora input durante l'animazione
      if (e.key === "ArrowLeft") {
        setSelectedBet(prev => Math.max(prev - 1, 0));
      } else if (e.key === "ArrowRight") {
        setSelectedBet(prev => Math.min(prev + 1, timeSlot));
      } else if (e.key.toLowerCase() === "a") {
        // Avvia l'animazione della slot
        setAnimating(true);
        // Simula l'animazione per 2 secondi e poi invoca la callback
        setTimeout(() => {
          onSlotComplete(selectedBet);
        }, 2000);
      }
    };

    window.addEventListener("keydown", handleKeyDown);
    return () => window.removeEventListener("keydown", handleKeyDown);
  }, [animating, selectedBet, timeSlot, onSlotComplete]);

  return (
    <div className="slot-overlay">
      <div className={`slot-container ${animating ? 'animate' : ''}`}>
        <h2>Slot Game</h2>
        <p>Tempo residuo (monete): {timeSlot}</p>
        <p>Puntata selezionata: {selectedBet}</p>
        {!animating ? (
          <p className="instructions">
            Usa le frecce per modificare la puntata e premi A per confermare.
          </p>
        ) : (
          <p className="animation-text">Animazione in corso...</p>
        )}
      </div>
    </div>
  );
}

export default SlotTime;
