import React, { useState } from 'react';
import './App.css';
import GameP from './components/GameP';
import HomeScreen from './components/HomeScreen';
import Rules from './components/Rules';
import Leaderboard from './components/Leaderboard';

function App() {
  // Stato per gestire quale "schermo" visualizzare:
  // 'home', 'game', 'rules' o 'leaderboard'
  const [screen, setScreen] = useState("home");

  const handlePlay = () => {
    setScreen("game");
  };

  const handleRules = () => {
    setScreen("rules");
  };

  const handleLeaderboard = () => {
    setScreen("leaderboard");
  };

  const handleBackToHome = () => {
    setScreen("home");
  };

  // Switch per scegliere il componente in base allo state
  switch (screen) {
    case "game":
      return <GameP />;
    case "rules":
      return <Rules onBack={handleBackToHome} />;
    case "leaderboard":
      return <Leaderboard onBack={handleBackToHome} />;
    default:
      return (
        <HomeScreen 
          onPlay={handlePlay} 
          onRules={handleRules}
          onLeaderboard={handleLeaderboard}
        />
      );
  }
}

export default App;
