// HomeP.jsx
import React, { useState, useCallback, useEffect } from "react";
import EmuSchermo from "./EmuSchermo";
import "./HomeP.css";

const BUTTONS = {
  A: 'A',
  B: 'B',
  START: 'START',
  SELECT: 'SELECT',
  UP: 'UP',
  DOWN: 'DOWN',
  LEFT: 'LEFT',
  RIGHT: 'RIGHT'
};

function HomeP() {
  const [inputQueue, setInputQueue] = useState([]);

  const handleInput = useCallback((button) => {
    setInputQueue(prev => [...prev, button]);
    setTimeout(() => {
      setInputQueue(prev => prev.filter(b => b !== button));
    }, 50);
  }, []);

  // Gestione tastiera
  useEffect(() => {
    const keyMapping = {
      'a': BUTTONS.A,
      'b': BUTTONS.B,
      'enter': BUTTONS.START,
      'backspace': BUTTONS.SELECT,
      'arrowup': BUTTONS.UP,
      'arrowdown': BUTTONS.DOWN,
      'arrowleft': BUTTONS.LEFT,
      'arrowright': BUTTONS.RIGHT,
      ' ': BUTTONS.UP
    };

    const handleKeyPress = (e) => {
      const button = keyMapping[e.key.toLowerCase()];
      if (button) {
        e.preventDefault();
        handleInput(button);
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [handleInput]);

  return (
    <div className="emuContainer">
      {/* Layout Portrait */}
      <div className="portraitLayout">
        <div className="screenContainer">
          <EmuSchermo inputQueue={inputQueue} />
        </div>

        <div className="controlsRow">
          <div className="dpadContainer">
            <div className="dpad">
              <button className="arrow up" onClick={() => handleInput(BUTTONS.UP)} aria-label="Up"></button>
              <button className="arrow left" onClick={() => handleInput(BUTTONS.LEFT)} aria-label="Left"></button>
              <button className="arrow right" onClick={() => handleInput(BUTTONS.RIGHT)} aria-label="Right"></button>
              <button className="arrow down" onClick={() => handleInput(BUTTONS.DOWN)} aria-label="Down"></button>
            </div>
          </div>

          <div className="actionButtonsContainer">
            <div className="actionButtons">
              <button className="red-button" onClick={() => handleInput(BUTTONS.A)} aria-label="A">A</button>
              <button className="blue-button" onClick={() => handleInput(BUTTONS.B)} aria-label="B">B</button>
            </div>
          </div>
        </div>

        <div className="startSelectContainer">
          <button className="select-button" onClick={() => handleInput(BUTTONS.SELECT)}>SELECT</button>
          <button className="start-button" onClick={() => handleInput(BUTTONS.START)}>START</button>
        </div>
      </div>

      {/* Layout Landscape */}
      <div className="landscapeLayout">
        <div className="controlsRowLandscape">
          <div className="dpadContainer">
            <div className="dpad">
              <button className="arrow up" onClick={() => handleInput(BUTTONS.UP)} aria-label="Up"></button>
              <button className="arrow left" onClick={() => handleInput(BUTTONS.LEFT)} aria-label="Left"></button>
              <button className="arrow right" onClick={() => handleInput(BUTTONS.RIGHT)} aria-label="Right"></button>
              <button className="arrow down" onClick={() => handleInput(BUTTONS.DOWN)} aria-label="Down"></button>
            </div>
          </div>

          <div className="screenContainer">
            <EmuSchermo inputQueue={inputQueue} />
          </div>

          <div className="actionButtonsContainer">
            <div className="actionButtons">
              <button className="red-button" onClick={() => handleInput(BUTTONS.A)} aria-label="A">A</button>
              <button className="blue-button" onClick={() => handleInput(BUTTONS.B)} aria-label="B">B</button>
            </div>
          </div>
        </div>

        <div className="startSelectContainer">
          <button className="select-button" onClick={() => handleInput(BUTTONS.SELECT)}>SELECT</button>
          <button className="start-button" onClick={() => handleInput(BUTTONS.START)}>START</button>
        </div>
      </div>
    </div>
  );
}

export default HomeP;
