import React, { useState, useCallback, useEffect } from "react";
import EmuSchermo from "./EmuSchermo";
import "./HomeP.css";

const BUTTONS = {
  A: 1,
  B: 2,
  START: 3,
  SELECT: 4,
  UP: 5,
  DOWN: 6,
  LEFT: 7,
  RIGHT: 8,
};

function HomeP() {
  const [buttonPressed, setButtonPressed] = useState(null);

  const handleInput = useCallback((button) => {
    setButtonPressed(button); // Aggiorna lo stato per passarlo a EmuSchermo

    switch (button) {
      case BUTTONS.A:
        console.log("A pressed");
        break;
      case BUTTONS.B:
        console.log("B pressed");
        break;
      case BUTTONS.START:
        console.log("Start pressed");
        break;
      case BUTTONS.SELECT:
        console.log("Select pressed");
        break;
      case BUTTONS.UP:
        console.log("Up pressed");
        break;
      case BUTTONS.DOWN:
        console.log("Down pressed");
        break;
      case BUTTONS.LEFT:
        console.log("Left pressed");
        break;
      case BUTTONS.RIGHT:
        console.log("Right pressed");
        break;
      default:
        console.warn("Input non riconosciuto");
        break;
    }
  }, []);

  // Gestione input da tastiera
  useEffect(() => {
    const handleKeyPress = (e) => {
      switch (e.key.toLowerCase()) {
        case "a":
          return handleInput(BUTTONS.A);
        case "b":
          return handleInput(BUTTONS.B);
        case "enter":
          return handleInput(BUTTONS.START);
        case "backspace":
          return handleInput(BUTTONS.SELECT);
        case "arrowup":
          return handleInput(BUTTONS.UP);
        case "arrowdown":
          return handleInput(BUTTONS.DOWN);
        case "arrowleft":
          return handleInput(BUTTONS.LEFT);
        case "arrowright":
          return handleInput(BUTTONS.RIGHT);
        default:
          break;
      }
    };

    window.addEventListener("keydown", handleKeyPress);
    return () => window.removeEventListener("keydown", handleKeyPress);
  }, [handleInput]);

  // Funzione per gestire il press visivo
  const handleButtonPress = (button) => (e) => {
    e.preventDefault();
    handleInput(button);
  };


  return (
    <div className="emuContainer">
      {/* Portrait Layout */}
      <div className="portraitLayout">
        <div className="screenContainer">
          <div className="windowEmu">
            {/* Passa lo stato buttonPressed a EmuSchermo */}
            <EmuSchermo button={buttonPressed} />
          </div>
        </div>

        <div className="controlsRow">
          <div className="dpadContainer">
            <div className="dpad">
              <button
                className="arrow up"
                onClick={handleButtonPress(BUTTONS.UP)}
                aria-label="Up"
              ></button>
              <button
                className="arrow left"
                onClick={handleButtonPress(BUTTONS.LEFT)}
                aria-label="Left"
              ></button>
              <button
                className="arrow right"
                onClick={handleButtonPress(BUTTONS.RIGHT)}
                aria-label="Right"
              ></button>
              <button
                className="arrow down"
                onClick={handleButtonPress(BUTTONS.DOWN)}
                aria-label="Down"
              ></button>
            </div>
          </div>

          <div className="actionButtonsContainer">
            <div className="actionButtons">
              <button
                className="red-button"
                onClick={handleButtonPress(BUTTONS.A)}
                aria-label="A Button"
              >
                A
              </button>
              <button
                className="blue-button"
                onClick={handleButtonPress(BUTTONS.B)}
                aria-label="B Button"
              >
                B
              </button>
            </div>
          </div>
        </div>

        <div className="startSelectContainer">
          <button
            className="select-button"
            onClick={handleButtonPress(BUTTONS.SELECT)}
          >
            SELECT
          </button>
          <button
            className="start-button"
            onClick={handleButtonPress(BUTTONS.START)}
          >
            START
          </button>
        </div>
      </div>

      {/* Landscape Layout */}
      <div className="landscapeLayout">
        <div className="controlsRowLandscape">
          <div className="dpadContainer">
            <div className="dpad">
              <button
                className="arrow up"
                onClick={handleButtonPress(BUTTONS.UP)}
                aria-label="Up"
              ></button>
              <button
                className="arrow left"
                onClick={handleButtonPress(BUTTONS.LEFT)}
                aria-label="Left"
              ></button>
              <button
                className="arrow right"
                onClick={handleButtonPress(BUTTONS.RIGHT)}
                aria-label="Right"
              ></button>
              <button
                className="arrow down"
                onClick={handleButtonPress(BUTTONS.DOWN)}
                aria-label="Down"
              ></button>
            </div>
          </div>

          <div className="screenContainer">
            <div className="windowEmu">
              <EmuSchermo button={buttonPressed} />
            </div>
          </div>

          <div className="actionButtonsContainer">
            <div className="actionButtons">
              <button
                className="red-button"
                onClick={handleButtonPress(BUTTONS.A)}
                aria-label="A Button"
              >
                A
              </button>
              <button
                className="blue-button"
                onClick={handleButtonPress(BUTTONS.B)}
                aria-label="B Button"
              >
                B
              </button>
            </div>
          </div>
        </div>

        <div className="startSelectContainer">
          <button
            className="select-button"
            onClick={handleButtonPress(BUTTONS.SELECT)}
          >
            SELECT
          </button>
          <button
            className="start-button"
            onClick={handleButtonPress(BUTTONS.START)}
          >
            START
          </button>
        </div>
      </div>
    </div>
  );
}

export default HomeP;
