  const ballSize = 4;
  const horizontalAcceleration = 0.1;
  const maxHorizontalSpeed = 1;
  const friction = 0.2;
  const jumpVelocity = 6;
  const gravity = -0.5;
  const [level, setLevel] = useState(1);
  const [LEVEL, SETLEVEL] = useState(generateLevel(1));
  const platformsRef = useRef(LEVEL);

  useEffect(() => {
    platformsRef.current = LEVEL;
  }, [LEVEL]);

  const [ball, setBall] = useState({
    x: 2,
    y: 0,
    vx: 0,
    vy: 0,
  });

  const pressedButtonsRef = useRef(pressedButtons);
  useEffect(() => {
    pressedButtonsRef.current = pressedButtons;
  }, [pressedButtons]);

  const animationFrameId = useRef(null);

  useEffect(() => {
    const update = () => {
      setBall((prev) => {
        let { x, y, vx, vy } = prev;
        const currentPressedButtons = pressedButtonsRef.current || {};

        // Gestione input orizzontale
        if (currentPressedButtons[BUTTONS.LEFT]) vx -= horizontalAcceleration;
        if (currentPressedButtons[BUTTONS.RIGHT]) vx += horizontalAcceleration;

        if (!currentPressedButtons[BUTTONS.LEFT] && !currentPressedButtons[BUTTONS.RIGHT]) {
          vx = vx > 0 ? Math.max(vx - friction, 0) : Math.min(vx + friction, 0);
        }

        vx = Math.min(Math.max(vx, -maxHorizontalSpeed), maxHorizontalSpeed);

        // Salto solo se a terra o su piattaforma
        const isOnGroundOrPlatform =
          prev.y === 0 ||
          platformsRef.current.some(p => {
            const platformTop = p.height + 2;
            const horizontalOverlap = (
              prev.x <= p.posEndX &&
              (prev.x + ballSize) >= p.posStartX
            );
            return prev.y === platformTop && horizontalOverlap;
          });

        if (currentPressedButtons[BUTTONS.A] && isOnGroundOrPlatform) {
          vy = jumpVelocity;
        }

        vy += gravity;
        let newY = y + vy;
        let newX = x + vx;

        // Collisioni con piattaforme
        let collided = false;

        platformsRef.current.forEach(platform => {
          const platformTop = platform.height + 2; // Top della piattaforma (2% di spessore)
          const platformBottom = platform.height;   // Base della piattaforma

          // 1. Controllo overlap orizzontale
          const horizontalOverlap = (
            newX < platform.posEndX &&
            (newX + ballSize) > platform.posStartX
          );

          // 2. Controllo overlap verticale
          const verticalOverlap = (
            newY <= platformTop &&
            (newY + ballSize) >= platformBottom
          );

          // 3. Determinazione direzione collisione
          if (horizontalOverlap && verticalOverlap) {
            const prevBottom = prev.y + ballSize;
            if (prevBottom <= platformBottom && vy > 0) {
              // Collisione dal basso (salto contro piattaforma)
              newY = platformBottom - ballSize;
              vy = 0;
            } else if (vy <= 0) {
              // Collisione dall'alto (atterraggio su piattaforma)
              newY = platformTop;
              vy = 0;
            }
            collided = true;
          }
        });

        // Collisione con il terreno solo se non abbiamo colliso con piattaforme
        if (!collided && newY < 0) {
          newY = 0;
          vy = 0;
        }

        // Clamping orizzontale
        newX = Math.max(0, Math.min(newX, 100 - ballSize));

        platformsRef.current.forEach(platform => {
          if (platform.type === "s") {
            const spikeLeft = platform.posStartX;
            const spikeRight = platform.posEndX;
            const spikeTop = platform.height + 4;
            const spikeBottom = platform.height;

            const horizontalCollision = (
              x + ballSize > spikeLeft &&
              x < spikeRight
            );

            const verticalCollision = (
              y  < spikeTop &&
              y + ballSize  > spikeBottom &&
              vy <= 0
            );

            if (horizontalCollision && verticalCollision) {
              newX = 2;
              newY = 0;
              vx = 0;
              vy = 0;
              SETLEVEL(generateLevel(level));
            }
          }
          if (platform.type === "T") {
            const spikeLeft = platform.posStartX;
            const spikeRight = platform.posEndX;
            const spikeTop = platform.height + 3;
            const spikeBottom = platform.height ;

            const horizontalCollision = (
              x + ballSize > spikeLeft &&
              x < spikeRight
            );

            const verticalCollision = (
              y < spikeTop &&
              y + ballSize > spikeBottom &&
              vy <= 0
            );

            if (horizontalCollision && verticalCollision) {
              const newLevel = level + 1;
              setLevel(newLevel);
              SETLEVEL(generateLevel(newLevel)); // Aggiorna lo stato correttamente
              newX = 2;
              newY = 0;
              vx = 0;
              vy = 0;
            }
          }
        });
        return { x: newX, y: newY, vx, vy };
      });
      animationFrameId.current = requestAnimationFrame(update);
    };

    animationFrameId.current = requestAnimationFrame(update);
    return () => cancelAnimationFrame(animationFrameId.current);
  }, [level, ballSize, horizontalAcceleration, friction, maxHorizontalSpeed, jumpVelocity, gravity]);

  return (
    <div className="emu-container">
      <div
        className="ball"
        style={{
          left: `${ball.x}%`,
          bottom: `${ball.y}%`,
          width: `${ballSize}%`,
          height: `${ballSize + 2}%`,
        }}
      ></div>

      {LEVEL.map((item, i) => {
        if (item.type === "T") {
          return (
            <div
              key={i}
              className="t-platform"
              style={{
                left: `${item.posStartX}%`,
                width: `${item.posEndX - item.posStartX}%`,
                bottom: `${item.height}%`,
                height: "2%",
              }}
            />
          );
        } else if (item.type === "p") {
          return (
            <div
              key={i}
              className="platform"
              style={{
                left: `${item.posStartX}%`,
                width: `${item.posEndX - item.posStartX}%`,
                bottom: `${item.height}%`,
                height: "2%",
              }}
            />
          );
        } else {
          return (
            <div
              key={i}
              className="spike"
              style={{
                left: `${item.posStartX}%`,
                width: `${item.posEndX - item.posStartX}%`,
                bottom: `${item.height}%`,
                height: "2%",
              }}
            />
          );
        }
      })}
    </div>
  );
}

export default EmuSchermo;
