import React, { useState, useEffect, useRef } from "react";
import "./EmuSchermo.css";

const BUTTONS = {
  A: 1,
  B: 2,
  START: 3,
  SELECT: 4,
  LEFT: 7,
  RIGHT: 8,
};

const LEVEL=[
    {type: "p", height:10, posStartX:30, posEndX:40},
    {type: "p", height:35, posStartX:50, posEndX:60},
    {type: "p", height:600, posStartX:70, posEndX:80}
]

function EmuSchermo({ pressedButtons }) {
  // Costanti di fisica espresse in percentuali
  const ballSize = 5;               // dimensione della pallina (in % del container)
  const horizontalAcceleration = 0.2; // accelerazione orizzontale per frame
  const maxHorizontalSpeed = 2;     // velocità massima orizzontale (% per frame)
  const friction = 0.1;             // attrito orizzontale quando non c'è input
  const jumpVelocity = 6;          // velocità iniziale del salto (% per frame)
  const gravity = -0.5;             // accelerazione di gravità (% per frame)

  // Stato della pallina:
  // x e y sono posizioni in percentuale,
  // vx e vy sono le velocità orizzontale e verticale rispettivamente.

  const [ball, setBall] = useState({
    x: 50 - ballSize / 2, // centrata orizzontalmente
    y: 0,                 // sul "terreno"
    vx: 0,
    vy: 0,
  });

  // Usiamo un ref per tenere traccia dell'ultimo valore di pressedButtons
  const pressedButtonsRef = useRef(pressedButtons);
  useEffect(() => {
    pressedButtonsRef.current = pressedButtons;
  }, [pressedButtons]);

  // Ciclo di animazione continuo con requestAnimationFrame
  const animationFrameId = useRef(null);


  useEffect(() => {
    const update = () => {
      setBall(prev => {
        let { x, y, vx, vy } = prev;
        const currentPressedButtons = pressedButtonsRef.current || {};

        // Gestione dell'accelerazione orizzontale
        if (currentPressedButtons[BUTTONS.LEFT]) {
          vx -= horizontalAcceleration;
        } 
        if (currentPressedButtons[BUTTONS.RIGHT]) {
          vx += horizontalAcceleration;
        } 

        // Se non ci sono input orizzontali, applica attrito
        if (!currentPressedButtons[BUTTONS.LEFT] && !currentPressedButtons[BUTTONS.RIGHT]) {
          // Applica attrito per rallentare gradualmente
          if (vx > 0) {
            vx = Math.max(vx - friction, 0);
          } else if (vx < 0) {
            vx = Math.min(vx + friction, 0);
          }
        }

        // Limita la velocità orizzontale
        if (vx > maxHorizontalSpeed) vx = maxHorizontalSpeed;
        if (vx < -maxHorizontalSpeed) vx = -maxHorizontalSpeed;


        // Gestione per il pulsante A (salto)
        if (currentPressedButtons[BUTTONS.A] && y === 0) {
          vy = jumpVelocity; 
        }

        // Applica la gravità alla velocità verticale
        vy += gravity;

        // Aggiorna le posizioni
        x += vx;
        y += vy;

        // Clamping della posizione orizzontale nel container
        if (x < 0) {
          x = 0;
          vx = 0;
        }
        if (x > 100 - ballSize) {
          x = 100 - ballSize;
          vx = 0;
        }

        // Gestione del "terreno": la pallina non può andare sotto y=0
        if (y < 0) {
          y = 0;
          vy = 0;
        }

        // Gestione dei pulsanti START e SELECT
        if (currentPressedButtons[BUTTONS.START]) {
          // Resetta la posizione della pallina al centro
          x = 50 - ballSize / 2;
          y = 0;
          vx = 0;
          vy = 0;
        }

        if (currentPressedButtons[BUTTONS.SELECT]) {
          // Cambia il colore della pallina o applica un effetto speciale
          // Questo richiederebbe un nuovo stato per il colore/effetto
        }

        // Supporto per la pressione simultanea di pulsanti (es. diagonali)
        // Già gestito naturalmente dal modo in cui applichiamo le forze

        // Supporto per la pressione DOWN (abbassarsi o accelerare la caduta)
        if (currentPressedButtons[BUTTONS.DOWN] && y > 0) {
          vy -= 0.5; // Accelera la caduta
        }

        return { x, y, vx, vy };
      });
      animationFrameId.current = requestAnimationFrame(update);
    };

    animationFrameId.current = requestAnimationFrame(update);
    return () => cancelAnimationFrame(animationFrameId.current);
  }, []);

  return (
    <div className="emu-container">
      <div
        className="ball"
        style={{
          left: `${ball.x}%`,
          bottom: `${ball.y}%`,
        }}
      ></div>
    </div>
  );
}

export default EmuSchermo;
