// HomeP.jsx
import React, { useState, useCallback, useEffect } from "react";
import EmuSchermo from "./EmuSchermo";
import "./HomeP.css";

const BUTTONS = {
  A: 'A',
  B: 'B',
  START: 'START',
  SELECT: 'SELECT',
  UP: 'UP',
  DOWN: 'DOWN',
  LEFT: 'LEFT',
  RIGHT: 'RIGHT'
};

function HomeP() {
  const [activeInputs, setActiveInputs] = useState(new Set());

  const handleInput = useCallback((button, isPressed) => {
    setActiveInputs(prev => {
      const newSet = new Set(prev);
      isPressed ? newSet.add(button) : newSet.delete(button);
      return newSet;
    });
  }, []);

  // Gestione tastiera
  useEffect(() => {
    const keyMapping = {
      'a': BUTTONS.A,
      'b': BUTTONS.B,
      'enter': BUTTONS.START,
      'backspace': BUTTONS.SELECT,
      'arrowup': BUTTONS.UP,
      'arrowdown': BUTTONS.DOWN,
      'arrowleft': BUTTONS.LEFT,
      'arrowright': BUTTONS.RIGHT,
      ' ': BUTTONS.UP // Spazio per il salto
    };

    const handleKeyDown = (e) => {
      const button = keyMapping[e.key.toLowerCase()];
      if (button) {
        e.preventDefault();
        handleInput(button, true);
      }
    };

    const handleKeyUp = (e) => {
      const button = keyMapping[e.key.toLowerCase()];
      if (button) {
        e.preventDefault();
        handleInput(button, false);
      }
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('keyup', handleKeyUp);
    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('keyup', handleKeyUp);
    };
  }, [handleInput]);

  return (
    <div className="emuContainer">
      {/* Layout Portrait */}
      <div className="portraitLayout">
        <div className="screenContainer">
          <EmuSchermo activeInputs={activeInputs} />
        </div>

        <div className="controlsRow">
          <div className="dpadContainer">
            <div className="dpad">
              <button 
                className="arrow up" 
                onMouseDown={() => handleInput(BUTTONS.UP, true)}
                onMouseUp={() => handleInput(BUTTONS.UP, false)}
                onTouchStart={() => handleInput(BUTTONS.UP, true)}
                onTouchEnd={() => handleInput(BUTTONS.UP, false)}
                aria-label="Up"
              ></button>
              <button 
                className="arrow left" 
                onMouseDown={() => handleInput(BUTTONS.LEFT, true)}
                onMouseUp={() => handleInput(BUTTONS.LEFT, false)}
                onTouchStart={() => handleInput(BUTTONS.LEFT, true)}
                onTouchEnd={() => handleInput(BUTTONS.LEFT, false)}
                aria-label="Left"
              ></button>
              <button 
                className="arrow right" 
                onMouseDown={() => handleInput(BUTTONS.RIGHT, true)}
                onMouseUp={() => handleInput(BUTTONS.RIGHT, false)}
                onTouchStart={() => handleInput(BUTTONS.RIGHT, true)}
                onTouchEnd={() => handleInput(BUTTONS.RIGHT, false)}
                aria-label="Right"
              ></button>
              <button 
                className="arrow down" 
                onMouseDown={() => handleInput(BUTTONS.DOWN, true)}
                onMouseUp={() => handleInput(BUTTONS.DOWN, false)}
                onTouchStart={() => handleInput(BUTTONS.DOWN, true)}
                onTouchEnd={() => handleInput(BUTTONS.DOWN, false)}
                aria-label="Down"
              ></button>
            </div>
          </div>

          <div className="actionButtonsContainer">
            <div className="actionButtons">
              <button 
                className="red-button" 
                onMouseDown={() => handleInput(BUTTONS.A, true)}
                onMouseUp={() => handleInput(BUTTONS.A, false)}
                onTouchStart={() => handleInput(BUTTONS.A, true)}
                onTouchEnd={() => handleInput(BUTTONS.A, false)}
                aria-label="A"
              >A</button>
              <button 
                className="blue-button" 
                onMouseDown={() => handleInput(BUTTONS.B, true)}
                onMouseUp={() => handleInput(BUTTONS.B, false)}
                onTouchStart={() => handleInput(BUTTONS.B, true)}
                onTouchEnd={() => handleInput(BUTTONS.B, false)}
                aria-label="B"
              >B</button>
            </div>
          </div>
        </div>

        <div className="startSelectContainer">
          <button 
            className="select-button" 
            onMouseDown={() => handleInput(BUTTONS.SELECT, true)}
            onMouseUp={() => handleInput(BUTTONS.SELECT, false)}
            onTouchStart={() => handleInput(BUTTONS.SELECT, true)}
            onTouchEnd={() => handleInput(BUTTONS.SELECT, false)}
          >SELECT</button>
          <button 
            className="start-button" 
            onMouseDown={() => handleInput(BUTTONS.START, true)}
            onMouseUp={() => handleInput(BUTTONS.START, false)}
            onTouchStart={() => handleInput(BUTTONS.START, true)}
            onTouchEnd={() => handleInput(BUTTONS.START, false)}
          >START</button>
        </div>
      </div>

      {/* Layout Landscape */}
      <div className="landscapeLayout">
        {/* Aggiungere gli stessi handler per il layout landscape */}
      </div>
    </div>
  );
}

export default HomeP;
