import React, { useState, useEffect, useRef } from "react";
import "./EmuSchermo.css";

// Costanti per la fisica della pallina
const horizontalAcceleration = 0.2;
const maxHorizontalSpeed = 2;
const friction = 0.1;
const jumpVelocity = 7;
const gravity = -0.5;

function EmuSchermo() {
  // Stato della pallina: posizione, velocità, stato sul pavimento
  const [ball, setBall] = useState({
    x: 50, // posizionato al centro orizzontalmente (in percentuale)
    y: 0,  // inizia sul "pavimento"
    vx: 0,
    vy: 0,
    isOnGround: true,
  });

  // Riferimento per tenere traccia dei tasti premuti
  const keysPressedRef = useRef({});

  useEffect(() => {
    const handleKeyDown = (e) => {
      keysPressedRef.current[e.code] = true;
    };

    const handleKeyUp = (e) => {
      keysPressedRef.current[e.code] = false;
    };

    window.addEventListener("keydown", handleKeyDown);
    window.addEventListener("keyup", handleKeyUp);
    return () => {
      window.removeEventListener("keydown", handleKeyDown);
      window.removeEventListener("keyup", handleKeyUp);
    };
  }, []);

  const animationFrameRef = useRef(null);

  useEffect(() => {
    const update = () => {
      setBall(prev => {
        let { x, y, vx, vy, isOnGround } = prev;
        const keys = keysPressedRef.current;

        // Movimento orizzontale con le frecce
        if (keys["ArrowLeft"]) {
          vx -= horizontalAcceleration;
        }
        if (keys["ArrowRight"]) {
          vx += horizontalAcceleration;
        }
        // Applica attrito se nessun tasto laterale è premuto
        if (!keys["ArrowLeft"] && !keys["ArrowRight"]) {
          if (vx > 0) vx = Math.max(vx - friction, 0);
          if (vx < 0) vx = Math.min(vx + friction, 0);
        }
        // Limita la velocità orizzontale
        vx = Math.max(Math.min(vx, maxHorizontalSpeed), -maxHorizontalSpeed);

        // Salto: se viene premuta la barra spaziatrice e la pallina è sul pavimento
        if (keys["Space"] && isOnGround) {
          vy = jumpVelocity;
          isOnGround = false;
        }

        // Applica gravità
        vy += gravity;

        // Aggiorna le posizioni
        x += vx;
        y += vy;

        // Gestione della collisione con il "pavimento" (y = 0)
        if (y < 0) {
          y = 0;
          vy = 0;
          isOnGround = true;
        }

        // Limita il movimento orizzontale nel contenitore:
        // dato che la larghezza della pallina è il 5%, il valore massimo di x è 95%
        if (x < 0) {
          x = 0;
          vx = 0;
        }
        if (x > 95) {
          x = 95;
          vx = 0;
        }

        return { x, y, vx, vy, isOnGround };
      });
      animationFrameRef.current = requestAnimationFrame(update);
    };

    animationFrameRef.current = requestAnimationFrame(update);
    return () => cancelAnimationFrame(animationFrameRef.current);
  }, []);

  return (
    <div className="emu-container">
      <div
        className="ball"
        style={{
          left: `${ball.x}%`,
          bottom: `${ball.y}%`
        }}
      />
    </div>
  );
}

export default EmuSchermo;
